import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from 'styled-components'
import anime from 'animejs';

const Wrapper = styled.div`

    margin: 20px;
    .header:hover {
        border: 1px solid #1de9b6;
        color: #1de9b6;
    }

    .box:hover {
        border: 1px solid #1de9b6;
        color: #1de9b6;
    }
`

const InternalWrapper = styled.div`

    width: 100%;


`

const Header = styled.div`  

    border: 1px solid white;
    height: 40px;
    display: flex;
    justify-content: space-between;

    div {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%;
        color: white;
    }

    a {
        color: white;
        text-decoration: none;
    }

    a:hover{
       color: #1de9b6
    }

`

const Box = styled.div`

    // width: ${props => props.width + "px"};
    padding: 0 40px;
    border: ${props => (props.border ? "1px solid white" : "1px solid rgba(0,0,0,0)")};
    margin: -1px;

`

const GridItem1 = styled.div`

    height: 200px;
    border: 1px solid #1de9b6;

`

const Bio = styled.div`

    display: flex;
    // flex-direction: column;
    justify-content: center;

    margin: 100px auto 75px;

    h1 {
        font-weight: normal;
        width: 400px;
        font-size: 30px;
        margin: auto 0 auto -100px;
        line-height: 50px;
        z-index: 100;
    }

    span {
        border-bottom: 3px solid #1de9b6;
    }

    span:hover {
        color: #1de9b6;
        border-bottom: 3px solid white;
    }

    // .headshot {
    //     height: 400px;
    //     width: 400px;
    //     mix-blend-mode: difference;
    //     margin: 20px auto;
    // }

`    

const Headshot = styled.div`

    background-image: url(img/headshot2.jpg);
    height: 500px;
    width: 500px;
    background-size: cover;
`

const ScrollCont = styled.div`

    height: 75px;

`       

const ScrollDude = styled.div`

    width: 1px;
    margin: 0 auto;
    animation: scrollDude 1s infinite;
    background-color: #1de9b6;
    height: 75px;


    @keyframes scrollDude {
        0% {
            height: 0px;
            opacity: 0;
            background-color: #1de9b6

        },
        10% {
            opacity: 1
        },
        50% {
            background-color: white;
        },
        80% {
            opacity: 1
        },
        100% {
            height: 100px;
            opacity: 0;
            background-color: #1de9b6
        }
    }

`

const PortfolioHeader = styled.div`

    height: 75px;
    border: 1px solid #1de9b6;
    background-color: #1de9b6;
    color: #642cff;
    display: flex;
    justify-content: center;
    align-items: center;
    font-style: italic;
    font-size: 60px;

`

const PortfolioItem = styled.div`

    height: 200px;
    border: 1px solid #1de9b6;

`

const Footer = styled.div`

    height: 75px;
    background-color:#1de9b6;

`

class Home extends Component {

    componentDidMount(){
        anime({
            targets: ".bio .el",
            color: "black",
            delay: anime.stagger(500),
            easing: "linear"
        })
    }

    render() {
        return (
            <Wrapper>
                <Header className="header">
                    <Box className="box" width={200} border={true}><a href="mailto:kevin@everysf.com">Kevin Macaraeg</a></Box>
                    <Box className="box" width={400} border={false}>San Francisco Based Front End 
                    Designer</Box>        
                    <Box className="box" width={400} border={true}><a href="https://www.linkedin.com/in/sandiegokevin/">LinkedIn</a></Box>       
                </Header>
                <InternalWrapper>
                    <Bio className="bio">
                        <Headshot className="headshot"></Headshot>
                        {/* <img className="headshot" src="img/headshot2.jpg"/> */}
                        <h1><span className="el">Kevin Macaraeg</span> is a <span className="el">front-end developer</span> with a strong background in <span className="el">design</span>, <span className="el">marketing</span>, and <span className="el">visual communications</span>.</h1>
                    </Bio>
                    <ScrollCont>                    <ScrollDude />
                    </ScrollCont>
                    <PortfolioHeader>Recent Work</PortfolioHeader>
                    <PortfolioItem>

                    </PortfolioItem>
                    {/* <GridItem1>

                    </GridItem1> */}
                </InternalWrapper>
                <Footer></Footer>
            </Wrapper>
        );
    }

}

export default Home;
