import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from 'styled-components'
import anime from 'animejs';

const Wrapper = styled.div`

    margin: 20px;
    .header:hover {
        border: 1px solid #1de9b6;
        color: #1de9b6;
    }

    .box:hover {
        border: 1px solid #1de9b6;
        color: #1de9b6;
    }
`

const InternalWrapper = styled.div`

    width: 100%;
    height: 500px;
    margin: 20px;


`

const Header = styled.div`  

    border: 1px solid white;
    height: 40px;
    display: flex;
    justify-content: space-between;

    div {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%;
        color: white;
    }

    a {
        color: white;
        text-decoration: none;
    }

    a:hover{
       color: #1de9b6
    }

`

const Box = styled.div`

    // width: ${props => props.width + "px"};
    padding: 0 40px;
    border: ${props => (props.border ? "1px solid white" : "1px solid rgba(0,0,0,0)")};
    margin: -1px;

`

const GridItem1 = styled.div`



`

const Bio = styled.div`

    display: flex;
    // flex-direction: column;
    justify-content: center;

    h1 {
        font-weight: normal;
        width: 400px;
        font-size: 30px;
        margin: auto 0 auto -100px;
        line-height: 50px;
        z-index: 100;
    }

    span {
        border-bottom: 3px solid #1de9b6;
    }

    span:hover {
        color: #1de9b6;
        border-bottom: 3px solid white;
    }

    // .headshot {
    //     height: 400px;
    //     width: 400px;
    //     mix-blend-mode: difference;
    //     margin: 20px auto;
    // }

`    

const Headshot = styled.div`

    background-image: url(img/headshot2.jpg);
    height: 500px;
    width: 500px;
    background-size: cover;
`

class Home extends Component {

    componentDidMount(){

    }

    render() {
        return (
            <Wrapper>
                <Header className="header">
                    <Box className="box" width={200} border={true}>Kevin Macaraeg</Box>
                    <Box className="box" width={400} border={false}>San Francisco Based Front End 
                    Designer</Box>        
                    <Box className="box" width={400} border={true}><a href="https://www.linkedin.com/in/sandiegokevin/">LinkedIn</a></Box>       
                </Header>
                <InternalWrapper>
                    <Bio>
                        <Headshot className="headshot"></Headshot>
                        {/* <img className="headshot" src="img/headshot2.jpg"/> */}
                        <h1><span>Kevin Macaraeg</span> is a <span>front-end developer</span> with a strong background in <span>design</span>, <span>marketing</span>, and <span>visual communications</span>.</h1>
                    </Bio>
                    <GridItem1>

                    </GridItem1>
                </InternalWrapper>
            </Wrapper>
        );
    }

}

export default Home;
