import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from 'styled-components'

const Wrapper = styled.div`

    margin: 20px;


`

const InternalWrapper = styled.div`

    width: 100%;
    height: 500px;
    margin: 20px;


`

const Header = styled.div`  

    border: 1px solid white;
    height: 40px;
    display: flex;
    justify-content: space-between;

    div {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%;
        color: white;
    }

    a {
        color: white;
        text-decoration: none;
    }

    a:hover{
       color: red
    }

`

const Box = styled.div`

    // width: ${props => props.width + "px"};
    padding: 0 40px;
    border: ${props => (props.border ? "1px solid white" : "1px solid rgba(0,0,0,0)")};
    margin: -1px;

`

const GridItem1 = styled.div`



`

const Bio = styled.div`

    display: flex;
    // flex-direction: column;
    justify-content: center;

    h1 {
        font-weight: normal;
        width: 500px;
        font-size: 20px;
    }

    // .headshot {
    //     height: 400px;
    //     width: 400px;
    //     mix-blend-mode: difference;
    //     margin: 20px auto;
    // }

`    

const Headshot = styled.div`

    background-image: url(img/headshot2.jpg);
    height: 500px;
    width: 500px;
    background-size: cover;
`

class Home extends Component {
    render() {
        return (
            <Wrapper>
                <Header>
                    <Box width={200} border={true}>Kevin Macaraeg</Box>
                    <Box width={400} border={false}>San Francisco Based Front End 
                    Designer</Box>        
                    <Box width={400} border={true}><a href="https://www.linkedin.com/in/sandiegokevin/">LinkedIn</a></Box>       
                </Header>
                <InternalWrapper>
                    <Bio>
                        <Headshot className="headshot"></Headshot>
                        {/* <img className="headshot" src="img/headshot2.jpg"/> */}
                        <h1>Kevin Macaraeg is a front-end developer with a strong background in design, marketing, and visual communications.</h1>
                    </Bio>
                    <GridItem1>

                    </GridItem1>
                </InternalWrapper>
            </Wrapper>
        );
    }

}

export default Home;
