import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs';

const Wrapper = styled.div`

    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;

`

const ArrCont = styled.div` 

    display: flex;
    align-items: center;
    overflow: hidden;

` 

const DynamicItem = styled.div`

    height: 500px;
    width: 20px;
    background-color: #1de9b6;
    opacity: 0;
    margin: 5px;

`

const Kevin = styled.div`

    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    text-align: center;

`

class Experimental extends Component {

    state= {

    }

    runAnime() {

        var tl = anime.timeline({
            easing: 'easeOutExpo',
            duration: 750
          });


        tl.add(
            {
              targets: '.container .el',
              opacity: 1,
              delay: anime.stagger(30),
              easing: 'cubicBezier(.5, .05, .1, .3)'
            }).add({
                targets: '.container .el',
                height: "100px",
                margin: "0px",
                delay: anime.stagger(30),
                easing: 'cubicBezier(.5, .05, .1, 1)'
            }).add({
                targets: '.container .el',
                height: "500px",
                delay: anime.stagger(50),
                easing: 'cubicBezier(.5, .05, .1, 1)'
            }).add({
                targets: '.container .el',
                margin: "20px",
                delay: anime.stagger(70),
                easing: 'cubicBezier(.5, .05, .1, 1)'
            }).add({
                targets: '.container .el',
                margin: "0px",
                width: "60px",
                delay: anime.stagger(150),
                easing: 'cubicBezier(.5, .05, .1, 1)'
            }).add({
                targets: '.name',
                letterSpacing: "5px",
                easing: 'cubicBezier(.5, .05, .1, .3)'
            }, '-=500')

        var tl2 = anime.timeline({
            easing: 'easeOutExpo',
            duration: 750
          });

    }

    componentDidMount() {
        console.log("Hey, thanks for visiting. Let me know if you want to work together, email me at kevin@everysf.com. Cheers")
        this.runAnime()
    }

    makeArr = () => {
        let arr = [];
        for (var i = 0; i < 30; i++) {
          arr.push(<DynamicItem className="el"/>)
        }
        return arr
      }

    render() {
        return (
            <Wrapper>
                <ArrCont className="container">
                    {this.makeArr()}
                </ArrCont>
                <Kevin className="name">
                    KEVIN MACARAEG
                </Kevin>
            </Wrapper>
        );
    }

}

export default Experimental;
