import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs'
import Typing from 'react-typing-animation';

const Wrapper = styled.div`

    height: 100vh;
    width: 100vw;
    overflow: hidden;
    background: black
    h1, h2, h3, p {
        font-size: 15px;
        font-weight: normal;
        margin: 0;
    }

`
const BackgroundImage = styled.div`

    height: 100vh;
    width: 100vw;
    overflow: hidden;
    background-image: url(img/abs1.jpg);
    background-size: cover;
    position: absolute;
    z-index: 1;

`

const MessageWrapper = styled.div`

    position: fixed;
    left: 50%;
    top: 50%;
    z-index: 10;
    transform: translate(-50%, -50%);

`

const MessageBox = styled.div`

    width: 500px;
    border: 1px solid white;

`

const MessageHeader = styled.div`

    border-bottom: 1px solid white;
    padding: 10px;
`

const MessageContents = styled.div`

    padding: 10px;

`

const Links = styled.div`

    display: flex;

    a {
        color: white;
        text-decoration: none;
        margin: 10px; 
        transition: .2s ease;
    }

    a:hover {
        color: black;
        letter-spacing: 4px;
    }
`

class Exp3 extends Component {

    state = {

    }

    componentDidUpdate() {

    }

    componentDidMount() {

        var tl = anime.timeline({
            easing: 'easeOutExpo',
            duration: 750,

        })

        tl.add({
            targets: ".backgroundShape",
            points: [
                { value: ["1000,1000 500,1000 0,1000 0,0 1000,0", "1000,1000 500,0 0,1000 0,0 1000,0"] },
            ],
            easing: 'spring(1, 80, 10, 0)',
            duration: 3000,
        })
    }

    render() {
        return (
            <Wrapper>
                <MessageWrapper>
                    <MessageBox>
                        <MessageHeader>
                            <h1>It is very nice to meet you</h1>
                        </MessageHeader>
                        <MessageContents>
                            <Typing speed={10}>Welcome to the portfolio of Kevin Macaraeg.<br></br>
                                <Typing.Delay ms={1000} />
                                ...<br></br>
                                <Typing.Delay ms={2000} />
                                Right now, he is at a coworking space, coffee shop, or at home in San Francisco designing, coding, or playing music.<br></br>
                                <Typing.Delay ms={3000} />
                                ...<br></br>
                                <Typing.Delay ms={4000} />
                                Are you curious?
                        </Typing>
                        </MessageContents>
                    </MessageBox>
                    <Links>
                        <a href="#">Design</a>
                        <a href="#">Code</a>
                        <a href="#">Music</a>
                        <a href="#">Resume</a>
                    </Links>
                </MessageWrapper>
                <BackgroundImage />
            </Wrapper>
        );
    }

}

export default Exp3;
