import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from 'styled-components'
import anime from 'animejs';

const Wrapper = styled.div`

    margin: 20px;
    .header:hover {
        border: 1px solid #1de9b6;
        color: #1de9b6;
    }

    .box:hover {
        border: 1px solid #1de9b6;
        color: #1de9b6;
    }

    a {
        color: white;
        text-decoration: none;
    }

    a:hover{
       color: #1de9b6
    }

    .portfolioHeader:hover {
        color: white;
    }

    .desktopOnly {
        @meda (max-width: 800px) {
            display: none;
        }
    }

    .portfolioItem:hover {
        background-color: #1de9b6;
        color: #642cff;

        h1{
            color: white;
        }
        a {
            color: #642cff;
        }
        a:hover {
            margin-top: 5px;
            color: white;
        }

        .previewTiles {
            // background-color: #642cff;
        }

        .box1 {
            // border: 1px solid #642cff;
        }

        .jobcontent {
            border-top: 1px solid #642cff;
        }
    }

    @media (max-width: 500px) {
        .portfolioItem {
            padding: 20px;
        }

        .previewTiles {
            margin: 0;
            overflow: hidden;
        }
    }

    // .contactMe {
    //     display: none;
    //     margin-left: 20px;
    //     transition: 1s ease;
    //     opacity: 0;

    // }

    // .contactLink:hover ~ .contactMe {
    //     display: flex;
    //     opacity: 1
    // }
`

const InternalWrapper = styled.div`

    width: 100%;
    margin-bottom: 20px;


`

const Header = styled.div`  

    border: 1px solid white;
    height: 40px;
    display: flex;
    justify-content: space-between;
    text-align: center;

    div {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%;
        color: white;

    }

    @media (max-width: 900px) {
        flex-direction: column;
        height: 160px;
        div {
            height: 60px;
        }
    }

    @media (max-width: 500px) {
        .bio {
            margin: 0 auto;
        }
    }

`

const Box = styled.div`

    // width: ${props => props.width + "px"};
    padding: 0 40px;
    border: ${props => (props.border ? "1px solid white" : "1px solid rgba(0,0,0,0)")};
    margin: -1px;

`

const GridItem1 = styled.div`

    height: 200px;
    border: 1px solid #1de9b6;

`

const Bio = styled.div`

    display: flex;
    // flex-direction: column;
    justify-content: center;

    margin: 100px auto 75px;

    h1 {
        font-weight: normal;
        width: 400px;
        font-size: 30px;
        margin: auto 0 auto -100px;
        line-height: 50px;
        z-index: 100;
    }

    span {
        border-bottom: 3px solid #1de9b6;
    }

    span:hover {
        color: #1de9b6;
        border-bottom: 3px solid white;
    }

    // .headshot {
    //     height: 400px;
    //     width: 400px;
    //     mix-blend-mode: difference;
    //     margin: 20px auto;
    // }

    @media (max-width: 500px) {
        flex-direction: column;
        margin: 40px auto 75px;
        h1 {
            margin: 0 auto;
            width: 100%;
            font-size: 20px;
            line-height: 30px;
        }
        .headshot {
            width: 100%;
            height: 300px;
            margin: 20px auto;
        }
    }
`

const Headshot = styled.div`

    background-image: url(img/headshot2.jpg);
    height: 500px;
    width: 500px;
    background-size: cover;
`

const ScrollCont = styled.div`

    height: 75px;

`

const ScrollDude = styled.div`

    width: 1px;
    margin: 0 auto;
    animation: scrollDude 1s infinite;
    background-color: #1de9b6;
    height: 75px;


    @keyframes scrollDude {
        0% {
            height: 0px;
            opacity: 0;
            background-color: #1de9b6

        },
        10% {
            opacity: 1
        },
        50% {
            background-color: white;
        },
        80% {
            opacity: 1
        },
        100% {
            height: 100px;
            opacity: 0;
            background-color: #1de9b6
        }
    }

`

const PortfolioHeader = styled.div`

    height: 30px;
    border: 5px solid #1de9b6;
    background-color: #1de9b6;
    color: #642cff;
    display: flex;
    justify-content: center;
    overflow: hidden;
    align-items: center;
    font-style: italic;
    font-weight: bold;
    font-size: 16px;
    letter-spacing: 30px;
    text-transform: uppercase;
    @media (max-width: 500px) {
        height: 50px;
        text-align: center;
        letter-spacing: 10px
    }

`

const PortfolioItem = styled.div`

    border: 1px solid #1de9b6;
    padding: 10px;
    display: flex;
    margin-top: -1px;

    h1 {
        margin: 0 0 12px 0;
        font-weight: normal;
    }

    p {
        margin: 5px 0 0 0;
        padding-left: 40px;
    }

    a {
        padding-left: 40px;
    }

    .year {
        margin: 0px 10px 10px 10px;
    }

    .jobcontent {
        margin: 10px 10px 10px 10px;
        border-top: 1px solid #1de9b6;
        width: 100%;
        padding-top: 10px;
    }

    .toolsUsed {
        padding: 5px;
        border: 1px solid #1de9b6;
    }

    .previewTiles {
        height: 100%;
        margin: 0 0 20px 0;
        .box1 {
            width: 550px;
            height: 380px;
            background-image: url(img/${props => props.image});
            background-size: contain;
            background-position: right;
            background-repeat: no-repeat;
            border: 1px solid rgba(0,0,0,0);
        }
        .box1:hover {
            opacity: .7
        }

        a {
            padding: 0;
            margin: 0;
        }
    }

    @media (max-width: 1100px) {
        .previewTiles {
            .box1 {
                width: 300px;
                height: 240px;
            }
        }


    }

    @media (max-width: 900px) {
        // flex-direction: column;

        .previewTiles {
            display: flex;
            flex-direction: row-reverse;
        }

        a {
            padding-left: 0px;
        }
        p {
            padding-left: 0;
        }
    }

    @media (max-width: 650px) {
        flex-direction: column-reverse;

        .previewTiles {

            justify-content: center;

            .box1 {
                width: 300px;
                height: 200px;
                background-image: url(img/${props => props.mobileimage});
                background-position: center;
            }
        }

        .jobcontent {
            margin: 0;
        }

        .year {
            margin: 10px 0 0 0;
        }
    }

`

const Footer = styled.div`

    margin-top: 30px;
    margin-bottom: 30px;
    height: 75px;
    border: 1px solid #1de9b6;
    display: flex;
    justify-content: center;
    align-items: center;

    a:hover {
        font-style: italic;
        letter-spacing: 5px;
    }

`

const Education = styled.div`

    border: 1px solid #1de9b6;
    text-align: center;
    font-size: 30px;

    h1 {
        background-color: #1de9b6;
    }

    p {
        padding: 80px;
        max-width: 7800px;
        margin: 0 auto;
        line-height: 50px;
    }


    span {
        border-bottom: 3px solid #1de9b6;
    }

    span:hover {
        color: #1de9b6;
        border-bottom: 3px solid white;
    }

    margin-bottom: 30px;

    @media (max-width: 500px) {
        font-size: 20px;
       p{
        line-height: 30px;
        padding: 20px;
        text-align: left;
       }
    }

`

