import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs';

const Wrapper = styled.div`

    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;

`

const ArrCont = styled.div` 

    display: flex;
    align-items: center;

` 

const DynamicItem = styled.div`

    height: 500px;
    width: 20px;
    background-color: #1de9b6;
    opacity: 0;
    margin: 5px;

`

class Experimental extends Component {

    state= {

    }

    runAnime() {

        var tl = anime.timeline({
            easing: 'easeOutExpo',
            duration: 750
          });


        tl.add(
            {
              targets: '.container .el',
              opacity: 1,
              delay: anime.stagger(10),
              easing: 'cubicBezier(.5, .05, .1, .3)'
            }).add({
                targets: '.container .el',
                height: "100px",
                delay: anime.stagger(10),
                easing: 'cubicBezier(.5, .05, .1, .3)'
            })
    }

    componentDidMount() {
        console.log("Hey, thanks for visiting. Let me know if you want to work together, email me at kevin@everysf.com. Cheers")
        this.runAnime()
    }

    makeArr = () => {
        let arr = [];
        for (var i = 0; i < 20; i++) {
          arr.push(<DynamicItem className="el"/>)
        }
        return arr
      }

    render() {
        return (
            <Wrapper>
                <ArrCont className="container">
                    {this.makeArr()}
                </ArrCont>
            </Wrapper>
        );
    }

}

export default Experimental;
