import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from 'styled-components'
import anime from 'animejs';

const Wrapper = styled.div`

    height: 100vh;
    width: 100vw;

`

const ArrCont = styled.div` 

    display: flex;

` 

const DynamicItem = styled.div`

    height: 100vh;
    width: 5vw;
    margin: 10px;
    background-color: red;

`

class Home extends Component {

    state= {

    }

    componentDidMount() {
        console.log("Hey, thanks for visiting. Let me know if you want to work together, email me at kevin@everysf.com. Cheers")
        anime(
            // {
            //   targets: '.container',
            //   display: 'flex',
            // },
          {
            targets: '.container .el',
            opacity: 1,
            delay: anime.stagger(5), // increase delay by 100ms for each elements.
            easing: 'cubicBezier(.5, .05, .1, .3)'
            // easing: 'steps(20)'
          })
    }

    makeArr = () => {
        let arr = [];
        for (var i = 0; i < 20; i++) {
          arr.push(<DynamicItem className="el"/>)
        }
        return arr
      }

    render() {
        return (
            <Wrapper>
                <ArrCont className="container">
                {this.makeArr()}
                </ArrCont>

            </Wrapper>
        );
    }

}

export default Home;
