import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs';

const Wrapper = styled.div`

    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: white !important;

`

const DynamicItem= styled.div`



`

class Fun extends Component {

    state= {
        animationRunning: false,
    }

    runAnime() {


            var tl = anime.timeline({
                easing: 'easeOutExpo',
                duration: 750,
                complete: function() {
                    // window.location.href = '/'
                  }
              });

            tl.add(
                {
                  targets: '.container .el',
                  height: '500px',
                  margin: '5px',
                  width: "10px",
                  opacity: 1,
                  rotate: 0,
                  delay: anime.stagger(200),
                  easing: 'steps(5)',
                }).add({
                    targets: '.container .el',
                    height: "100px",
                    margin: "0px",
                    backgroundColor: "white",
                    delay: anime.stagger(10, {direction: 'reverse'}),
                    easing: 'cubicBezier(.5, .05, .1, 1)',
                }, '+=500').add({
                    targets: '.container .el',
                    height: "500px",
                    easing: 'cubicBezier(.5, .05, .1, 1)'
                }, '+=500').add({
                    targets: '.container .el',
                    margin: "20px",
                    delay: anime.stagger(10),
                    easing: 'cubicBezier(.5, .05, .1, 1)'
                }, '+=600').add({
                    targets: '.container .el',
                    margin: "0px",
                    width: "60px",
                    delay: anime.stagger(50, {direction: 'reverse'}),
                    easing: 'cubicBezier(.5, .05, .1, 1)'
                }).add({
                    targets: '.name',
                    letterSpacing: "5px",
                    opacity: 1,
                    easing: 'cubicBezier(.5, .05, .1, .3)',
                    color: "#1de9b6"
                }).add({
                    targets: '.container .el',
                    margin: "0px",
                    height: "0px",
                    width: "10px",
                    rotate: anime.stagger([-360, 360]),
                    easing: 'cubicBezier(.5, .05, .1, 1)'
                }, '-=500').add({
                    targets: '.name',
                    opacity: 0,
                }, 0).add({
                    targets: '.welcome',
                    opacity: '0'
                }, 13500).add({
                    targets: '.welcome',
                    opacity: '1'
                }, 0)



    }

    componentDidMount() {
        console.log("Hey, thanks for visiting. Let me know if you want to work together, email me at kevin@everysf.com. Cheers")
        this.runAnime()
    }

    makeArr = () => {
        let arr = [];
        for (var i = 0; i < 30; i++) {
          arr.push(<DynamicItem className="el"/>)
        }
        return arr
      }

    render() {
        return (
            <Wrapper>

            </Wrapper>
        );
    }

}

export default Fun;
