import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs'

const Wrapper = styled.div`

    .el {
        opacity: .2

    }

    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;

    svg {
        width: 800px;
        margin: 0 auto;
    }

    .el:hover {
        fill: white;
        stroke: red;
    }

`


class Exp3 extends Component {

    state = {

    }

    componentDidUpdate() {

    }

    componentDidMount() {

        var tl = anime.timeline({
            easing: 'easeOutExpo',
            duration: 750,

        })

        tl.add({
            targets: ".wrapper .el",
            delay: anime.stagger(100, {grid: [4, 3], from: 'center'}),
            opacity: 1,
            easing: 'easeInOutSine',
          }).add({
            targets: ".wrapper .el",
            delay: anime.stagger(500),
            opacity: 1,
            strokeDashoffset: [anime.setDashoffset, 2000],
            easing: 'easeInOutSine',
            loop: true,
            direction: 'alternate',
          }, 0)


    }

    render() {
        return (
            <Wrapper className="wrapper">
                {this.svg()}
            </Wrapper >

        );
    }

}

export default Exp3;
