import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs'

const Wrapper = styled.div`

    height: 100vh;
    width: 100vw;
    overflow: hidden;
    background-color: white;

    #backgroundSVG {
        top: 0;
        position: fixed;
    }

`

let background = () => {
    return (
    <svg id="backgroundSVG"
        viewBox="0 0 1000 1000">
        <g>
            <polygon className="backgroundShape" points="1000,1000 500,1000 0,1000 0,0 1000,0" />
        </g>
    </svg>
    )
}


class Exp3 extends Component {

    state = {

    }

    componentDidUpdate() {

    }

    componentDidMount() {

        var tl = anime.timeline({
            easing: 'easeOutExpo',
            duration: 750,

        })

        tl.add({
            targets: ".backgroundShape",
            points: [
                { value: ["1000,1000 500,1000 0,1000 0,0 1000,0","1000,1000 500,0 0,1000 0,0 1000,0"]},
            ],
            easing: 'easeInOutSine',
        }, 2000).add({
            targets: ".backgroundShape",
            fill: "#fff000",
            easing: 'easeInOutSine',
            duration: 10000
        }, 2000)


    }

    render() {
        return (
            <Wrapper className="wrapper">
                {background()}
            </Wrapper >
        );
    }

}

export default Exp3;
