import React, { Component } from 'react';
import styled from 'styled-components'
import anime from 'animejs'

const Wrapper = styled.div`

    height: 100vh;
    width: 100vw;
    overflow: hidden;
    background-color: white;

`

class Exp3 extends Component {

    state = {

    }

    componentDidUpdate() {

    }

    componentDidMount() {

        var tl = anime.timeline({
            easing: 'easeOutExpo',
            duration: 750,

        })

        tl.add({
            targets: ".backgroundShape",
            points: [
                { value: ["1000,1000 500,1000 0,1000 0,0 1000,0","1000,1000 500,0 0,1000 0,0 1000,0"]},
            ],
            easing: 'spring(1, 80, 10, 0)',
            duration: 3000,
        })
    }

    render() {
        return (
            <Wrapper className="wrapper">

            </Wrapper >
        );
    }

}

export default Exp3;
